JC.Tree.dataFilter =
function( _data ){
    var _r = {};

    if( _data ){
        if( _data.root.length > 2 ){
            _data.root.shift();
            _r.root = _data.root;
         }
        _r.data = {};
        for( var k in _data.data ){
            _r.data[ k ] = [];
            for( var i = 0, j = _data.data[k].length; i < j; i++ ){
                if( _data.data[k][i].length < 3 ) continue;
                _data.data[k][i].shift();
                _r.data[k].push( _data.data[k][i] );
            }
        }
    }
    return _r;
};

